    BorderedRadio.displayName = "BorderedRadio";
    // @ts-ignore
    BorderedRadio.__docgenInfo = { "description": "A radio button with a border", "displayName": "BorderedRadio", "props": {} };
    // @ts-ignore
    if (typeof STORYBOOK_REACT_CLASSES !== "undefined")
