  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'
  import { Playground, PropsTable } from 'docz'
import { Value } from 'react-powerplug'
import { LargeSelect, SmallSelect } from '../src'

export default ({components, ...props}) => <MDXTag name="wrapper"  components={components}>
<MDXTag name="h1" components={components} props={{"id":"select"}}>{`Select`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"largeselect"}}>{`LargeSelect`}</MDXTag>
<Playground __position={0} __codesandbox={`undefined`} __code={`<Value>
  {({ value, set }) => (
    <LargeSelect
      options={[
        {
          text: \'Most Recent\',
          value: \'mostRecent\',
        },
        {
          text: \'Price\',
          value: \'price\',
        },
        {
          text: \'Estimate\',
          value: \'estimate\',
        },
      ]}
      selected={value}
      onSelect={value => set(value)}
    />
  )}
</Value>`} __scope={{props,Value,LargeSelect,SmallSelect}}>
  <Value>
    {({ value, set }) => (
      <LargeSelect
        options={[
          {
            text: "Most Recent",
            value: "mostRecent",
          },
          {
            text: "Price",
            value: "price",
          },
          {
            text: "Estimate",
            value: "estimate",
          },
        ]}
        selected={value}
        onSelect={value => set(value)}
      />
    )}
  </Value>
</Playground>
<MDXTag name="h2" components={components} props={{"id":"disabled-largeselect"}}>{`Disabled LargeSelect`}</MDXTag>
<Playground __position={1} __codesandbox={`undefined`} __code={`<Value>
  {({ value, set }) => (
    <LargeSelect
      options={[
        {
          text: \'Most Recent\',
          value: \'mostRecent\',
        },
        {
          text: \'Price\',
          value: \'price\',
        },
        {
          text: \'Estimate\',
          value: \'estimate\',
        },
      ]}
      selected={value}
      onSelect={value => set(value)}
      disabled
    />
  )}
</Value>`} __scope={{props,Value,LargeSelect,SmallSelect}}>
  <Value>
    {({ value, set }) => (
      <LargeSelect
        options={[
          {
            text: "Most Recent",
            value: "mostRecent",
          },
          {
            text: "Price",
            value: "price",
          },
          {
            text: "Estimate",
            value: "estimate",
          },
        ]}
        selected={value}
        onSelect={value => set(value)}
        disabled
      />
    )}
  </Value>
</Playground>
<MDXTag name="h2" components={components} props={{"id":"smallselect"}}>{`SmallSelect`}</MDXTag>
<Playground __position={2} __codesandbox={`undefined`} __code={`<Value>
  {({ value, set }) => (
    <SmallSelect
      options={[
        {
          text: \'Most Recent\',
          value: \'mostRecent\',
        },
        {
          text: \'Price\',
          value: \'price\',
        },
        {
          text: \'Estimate\',
          value: \'estimate\',
        },
      ]}
      selected={value}
      onSelect={value => set(value)}
    />
  )}
</Value>`} __scope={{props,Value,LargeSelect,SmallSelect}}>
  <Value>
    {({ value, set }) => (
      <SmallSelect
        options={[
          {
            text: "Most Recent",
            value: "mostRecent",
          },
          {
            text: "Price",
            value: "price",
          },
          {
            text: "Estimate",
            value: "estimate",
          },
        ]}
        selected={value}
        onSelect={value => set(value)}
      />
    )}
  </Value>
</Playground>
<MDXTag name="h2" components={components} props={{"id":"error-state"}}>{`Error State`}</MDXTag>
<Playground __position={3} __codesandbox={`undefined`} __code={`<Value>
  {({ value = \'badValue\', set }) => {
    const error = value === \'goodValue\' ? \'\' : \'Error Message\'
    return (
      <LargeSelect
        error={error}
        options={[
          {
            text: \'Good\',
            value: \'goodValue\',
          },
          {
            text: \'Bad\',
            value: \'badValue\',
          },
        ]}
        selected={value}
        onSelect={value => set(value)}
      />
    )
  }}
</Value>`} __scope={{props,Value,LargeSelect,SmallSelect}}>
