    {({ value="badValue", set }) => {
      const error = value === "goodValue" ? "" : "Error Message"
      return (
        <LargeSelect
          error={error}
          options={[
            {
              text: "Good",
              value: "goodValue",
            },
            {
              text: "Bad",
              value: "badValue",
            },
          ]}
          selected={value}
          onSelect={value => set(value)}
        />
      )
    }}
  </Value>
</Playground></MDXTag>
